INSERT INTO d2zv_posts (`post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES ('2', '2017-03-22 12:07:36', '2017-03-22 09:07:36', 'Audi A4 I (B5) (рестайл) (cедан) 1.6i (101Hp) 1999г.в. переоборудован для использования в качестве моторного топлива сжиженного нефтяного газа (пропан-бутан) с использованием системы - \'Тамона\' в феврале 2016г.\n1. Модель автомобиля: Audi A4 I (B5) (рестайл) (cедан)\n2. двигатель: рядный, бензиновый, 4-цилиндровый, рабочий объем 1.6л, мощность 101 л.с.\n3. годы выпуска: с 1997-2001\n4. рекомендованное оборудование для установки: BRC\n5. альтернативное оборудование для установки: Lovato, Digitronic, Alpha-M\n6. рекомендованный баллон: тороидальный баллон вместо запасного колеса объемом 53л. (630х225)\n7. альтернативный вариант установки баллона: цилиндрический баллон объемом до 90л. в багажном отделении вдоль заднего ряда сидений.\n8. установка внешнего заправочного устройство: под лючок бензобака рядом с заливной горловиной.\n9. показания датчика уровня выводятся на кнопку переключения режимов работы - газ\\бензин\n10. особенности установки: крайне компактная компоновка моторного отсека, отсутствие свободного места под капотом, как следствие - увеличенное время монтажа по сравнению с более простыми моделями. \n<div class=\'a21_slider_item\'>\n<a class=\"example-image-link\" href=\"http://www.gazunas.ru/upload/resize_cache/iblock/3f5/960_700_1/3f55c8f1923f1eb391025b6e4e4988b5.jpg\" data-lightbox=\"example-set_1\">\n                                <img class=\"example-image\" src=\"http://www.gazunas.ru/upload/resize_cache/iblock/3f5/122_91_1/3f55c8f1923f1eb391025b6e4e4988b5.jpg\" alt=\"Audi A4 I (B5) (рестайл) (cедан) 1.6i (101Hp) 1999г.в.\" title=\"A